// Detectar si es JSON de Comando o Texto Conversacional
const output = $('SIRA').first().json.output;

try {
  // Intentamos parsear. Si falla, es texto.
  const cleanJson = output.replace(/```json/g, '').replace(/```/g, '');
  const parsed = JSON.parse(cleanJson);

  if (parsed.tipo === 'comando') {
    return { ...parsed, isCommand: true };
  }
  return { message: output, isCommand: false };
} catch (e) {
  return { message: output, isCommand: false };
}

// Iteramos sobre todos los items de entrada
return $input.all().map(item => {
  const inputData = item.json;

  // 1. DETECCIÓN INTELIGENTE DEL CAMPO
  // Buscamos dónde está la carga útil. A veces viene en "message", otras en "mensaje:" (con dos puntos)
  // o incluso podría venir en "output" o "mensaje".
  let rawContent = inputData.message || inputData['mensaje:'] || inputData.mensaje || inputData.output;

  // Si no encontramos nada, devolvemos un error controlado
  if (!rawContent) {
    return { json: { mensaje: "Error: No se encontró el campo de mensaje en la entrada." } };
  }

  // 2. LIMPIEZA DE "SUCIEDAD" MARKDOWN
  // Si es un texto, eliminamos los ```json y ```
  if (typeof rawContent === 'string') {
    rawContent = rawContent
      .replace(/```json/gi, '') // Reemplaza ```json (insensible a mayúsculas)
      .replace(/```/g, '')      // Reemplaza ``` de cierre
      .trim();                  // Elimina espacios en blanco extra al inicio/final
  }

  // 3. PARSEO DEL JSON INTERNO (String -> Objeto)
  let innerObject;
  try {
    // Intentamos convertir el string limpio en un objeto real
    innerObject = (typeof rawContent === 'string') ? JSON.parse(rawContent) : rawContent;
  } catch (error) {
    // Si falla el parseo, asumimos que el texto ya era el mensaje final
    // y lo devolvemos limpio.
    return { json: { mensaje: rawContent } };
  }

  // 4. EXTRACCIÓN FINAL
  // Buscamos el texto del mensaje dentro del objeto parseado
  // (Soportamos que venga como "mensaje" o "message" dentro del JSON interno)
  const textoLimpio = innerObject.mensaje || innerObject.message || "No se encontró texto dentro del JSON.";

  // 5. RETORNO ESTRUCTURADO
  return {
    json: {
      mensaje: textoLimpio
    }
  };
});
